    /**
     * decode a resultset in an array of TbCountryBean objects
     *
     * @param rs the resultset to decode
     * @param fieldList table of the field's associated constants
     * @param startRow the start row to be used (first row = 1, last row = -1)
     * @param numRows the number of rows to be retrieved (all rows = a negative number)
     * @return the resulting TbCountryBean table
     * @throws DAOException
     */
    //28
    public TbCountryBean[] decodeResultSet(ResultSet rs, int[] fieldList, int startRow, int numRows) throws DAOException
    {
        List v = new ArrayList();
        try
        {
            if (rs.absolute(startRow) && numRows!=0)
            {
                int count = 0;
                if(fieldList == null) {
                    do
                    {
                        v.add(decodeRow(rs));
                        count++;
                    } while ( (count<numRows||numRows<0) && rs.next() );
                }
                else {
                    do
                    {
                        v.add(decodeRow(rs, fieldList));
                        count++;
                    } while ( (count<numRows||numRows<0) && rs.next() );
                }
            }
            return (TbCountryBean[])v.toArray(new TbCountryBean[v.size()]);
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        finally
        {
            if (v != null) { v.clear(); v = null;}
        }
    }

    /**
     * Transforms a ResultSet iterating on the tb_country on a TbCountryBean bean.
     *
     * @param rs the ResultSet to be transformed
     * @return bean resulting TbCountryBean bean
     * @throws DAOException
     */
    //29
    public TbCountryBean decodeRow(ResultSet rs) throws DAOException
    {
        TbCountryBean bean = this.createTbCountryBean();
        try
        {
            bean.setTbcName(rs.getString(1));
            bean.setTbcId(Manager.getInteger(rs, 2));
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }
	
    //29A
    public TbCountryBean toBean(TbCountryBeanModel beanModel, TbCountryBean bean)
    {
		if (beanModel.getTbcName() != null)
			bean.setTbcName(beanModel.getTbcName());

		if (bean.getTbcName() != null)
		if ("java.sql.Timestamp".equals(bean.getTbcName().getClass().getName()))
			bean.setTbcName(beanModel.getTbcName());
		
		if (beanModel.getTbcId() != null)
			bean.setTbcId(beanModel.getTbcId());

		if (bean.getTbcId() != null)
		if ("java.sql.Timestamp".equals(bean.getTbcId().getClass().getName()))
			bean.setTbcId(beanModel.getTbcId());
		
        return bean;
    }
	
