    /**
     * Transforms a ResultSet iterating on the tb_country table on a TbCountryBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TbCountryBean bean
     * @throws DAOException
     */
    //30
    public TbCountryBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbCountryBean bean = this.createTbCountryBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBC_NAME:
                        ++pos;
                        bean.setTbcName(rs.getString(pos));
                        break;
                    case ID_TBC_ID:
                        ++pos;
                        bean.setTbcId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

