    public TbCustomerBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbCustomerBean bean = this.createTbCustomerBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBC_DESCRIPTION:
                        ++pos;
                        bean.setTbcDescription(rs.getString(pos));
                        break;
                    case ID_TBC_NAME:
                        ++pos;
                        bean.setTbcName(rs.getString(pos));
                        break;
                    case ID_TBC_CUSTOMER_ID:
                        ++pos;
                        bean.setTbcCustomerId(rs.getString(pos));
                        break;
                    case ID_TBC_ID:
                        ++pos;
                        bean.setTbcId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

