    /**
     * Transforms a ResultSet iterating on the tb_employee_ptt table on a TbEmployeePttBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TbEmployeePttBean bean
     * @throws DAOException
     */
    //30
    public TbEmployeePttBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbEmployeePttBean bean = this.createTbEmployeePttBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBEPTT_NOMINAL:
                        ++pos;
                        bean.setTbepttNominal(Manager.getDouble(rs, pos));
                        break;
                    case ID_TBEPTT_CURRENCY_NAME:
                        ++pos;
                        bean.setTbepttCurrencyName(rs.getString(pos));
                        break;
                    case ID_TBEPTT_INSERT_TIME:
                        ++pos;
                        bean.setTbepttInsertTime(rs.getTimestamp(pos));
                        break;
                    case ID_TBC_ID:
                        ++pos;
                        bean.setTbcId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBE_ID:
                        ++pos;
                        bean.setTbeId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBPTT_ID:
                        ++pos;
                        bean.setTbpttId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBEPTT_ID:
                        ++pos;
                        bean.setTbepttId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

