    /**
     * Transforms a ResultSet iterating on the tb_employee_salary table on a TbEmployeeSalaryBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TbEmployeeSalaryBean bean
     * @throws DAOException
     */
    //30
    public TbEmployeeSalaryBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbEmployeeSalaryBean bean = this.createTbEmployeeSalaryBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBES_CURRENCY_NAME:
                        ++pos;
                        bean.setTbesCurrencyName(rs.getString(pos));
                        break;
                    case ID_TBES_PAY_FREQUENCY:
                        ++pos;
                        bean.setTbesPayFrequency(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBES_COGS:
                        ++pos;
                        bean.setTbesCogs(Manager.getDouble(rs, pos));
                        break;
                    case ID_TBES_BASIC_SALARY:
                        ++pos;
                        bean.setTbesBasicSalary(Manager.getDouble(rs, pos));
                        break;
                    case ID_TBC_ID:
                        ++pos;
                        bean.setTbcId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBP_ID:
                        ++pos;
                        bean.setTbpId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBE_ID:
                        ++pos;
                        bean.setTbeId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBES_ID:
                        ++pos;
                        bean.setTbesId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

