    /**
     * Transforms a ResultSet iterating on the tb_immigration table on a TbImmigrationBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TbImmigrationBean bean
     * @throws DAOException
     */
    //30
    public TbImmigrationBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbImmigrationBean bean = this.createTbImmigrationBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBI_COMMENTS:
                        ++pos;
                        bean.setTbiComments(rs.getString(pos));
                        break;
                    case ID_TBI_EXPIRY_DATE:
                        ++pos;
                        bean.setTbiExpiryDate(rs.getTimestamp(pos));
                        break;
                    case ID_TBI_ISSUED_DATE:
                        ++pos;
                        bean.setTbiIssuedDate(rs.getTimestamp(pos));
                        break;
                    case ID_TBN_ID:
                        ++pos;
                        bean.setTbnId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBI_L9_REVIEW_DATE:
                        ++pos;
                        bean.setTbiL9ReviewDate(rs.getTimestamp(pos));
                        break;
                    case ID_TBI_L9_STATUS:
                        ++pos;
                        bean.setTbiL9Status(rs.getString(pos));
                        break;
                    case ID_TBI_IMMIGRATION_NO:
                        ++pos;
                        bean.setTbiImmigrationNo(rs.getString(pos));
                        break;
                    case ID_TBI_IMMIGRATION_TYPE:
                        ++pos;
                        bean.setTbiImmigrationType(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBE_ID:
                        ++pos;
                        bean.setTbeId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

