    /**
     * Transforms a ResultSet iterating on the tb_job table on a TbJobBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TbJobBean bean
     * @throws DAOException
     */
    //30
    public TbJobBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbJobBean bean = this.createTbJobBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBJ_JOINED_DATE:
                        ++pos;
                        bean.setTbjJoinedDate(rs.getTimestamp(pos));
                        break;
                    case ID_TBL_ID:
                        ++pos;
                        bean.setTblId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBO_ID:
                        ++pos;
                        bean.setTboId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBP_ID:
                        ++pos;
                        bean.setTbpId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBEJC_ID:
                        ++pos;
                        bean.setTbejcId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBES_ID:
                        ++pos;
                        bean.setTbesId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBJT_ID:
                        ++pos;
                        bean.setTbjtId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBE_ID:
                        ++pos;
                        bean.setTbeId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

