    /**
     * Transforms a ResultSet iterating on the tb_kpi_assign table on a TbKpiAssignBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TbKpiAssignBean bean
     * @throws DAOException
     */
    //30
    public TbKpiAssignBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbKpiAssignBean bean = this.createTbKpiAssignBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBKA_SPV_ID:
                        ++pos;
                        bean.setTbkaSpvId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBKA_STATUS:
                        ++pos;
                        bean.setTbkaStatus(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBKA_POIN:
                        ++pos;
                        bean.setTbkaPoin(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBKA_ACTION:
                        ++pos;
                        bean.setTbkaAction(rs.getString(pos));
                        break;
                    case ID_TBKA_EVALUASI:
                        ++pos;
                        bean.setTbkaEvaluasi(rs.getString(pos));
                        break;
                    case ID_TBK_ID:
                        ++pos;
                        bean.setTbkId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBE_ID:
                        ++pos;
                        bean.setTbeId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBKA_ID:
                        ++pos;
                        bean.setTbkaId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

