    protected int fillWhere(StringBuffer sqlWhere, TbLicensesBean bean, int searchType)
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        String sqlEqualsOperation = "=";
        if (searchType != SEARCH_EXACT) {
            sqlEqualsOperation = " like ";
        }
        try
        {
            if (bean.isTblDescriptionModified()) {
                _dirtyCount ++;
                if (bean.getTblDescription() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_description IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_description ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isTblNameModified()) {
                _dirtyCount ++;
                if (bean.getTblName() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_name IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_name ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isTblLicensesIdModified()) {
                _dirtyCount ++;
                if (bean.getTblLicensesId() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_licenses_id IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_licenses_id ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isTblIdModified()) {
                _dirtyCount ++;
                if (bean.getTblId() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_id IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbl_id = ?");
                }
            }
        }
        finally
        {
            sqlEqualsOperation = null;
        }
        return _dirtyCount;
    }

