    //30
    public TbNegaraBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbNegaraBean bean = this.createTbNegaraBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBN_NAMA:
                        ++pos;
                        bean.setTbnNama(rs.getString(pos));
                        break;
                    case ID_TBN_ID:
                        ++pos;
                        bean.setTbnId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

