    /**
     * Transforms a ResultSet iterating on the tb_notification table on a TbNotificationBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TbNotificationBean bean
     * @throws DAOException
     */
    //30
    public TbNotificationBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TbNotificationBean bean = this.createTbNotificationBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBN_READ_DATE:
                        ++pos;
                        bean.setTbnReadDate(rs.getTimestamp(pos));
                        break;
                    case ID_TBN_SENT_DATE:
                        ++pos;
                        bean.setTbnSentDate(rs.getTimestamp(pos));
                        break;
                    case ID_TBN_CREATE_DATE:
                        ++pos;
                        bean.setTbnCreateDate(rs.getTimestamp(pos));
                        break;
                    case ID_TBN_DATA:
                        ++pos;
                        bean.setTbnData(rs.getString(pos));
                        break;
                    case ID_TBE_ID_TO:
                        ++pos;
                        bean.setTbeIdTo(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBE_ID_FROM:
                        ++pos;
                        bean.setTbeIdFrom(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBNU_ID:
                        ++pos;
                        bean.setTbnuId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBNT_ID:
                        ++pos;
                        bean.setTbntId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBNC_ID:
                        ++pos;
                        bean.setTbncId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBN_ID:
                        ++pos;
                        bean.setTbnId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

