    /**
     * fills the given stringbuffer with the sql where clausis constructed using the bean and the search type
     * @param sqlWhere the stringbuffer that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     */
    protected int fillWhere(StringBuffer sqlWhere, TbPtkpBean bean, int searchType)
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        String sqlEqualsOperation = "=";
        if (searchType != SEARCH_EXACT) {
            sqlEqualsOperation = " like ";
        }
        try
        {
            if (bean.isTbptkpJumlahModified()) {
                _dirtyCount ++;
                if (bean.getTbptkpJumlah() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_jumlah IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_jumlah = ?");
                }
            }
            if (bean.isTbptkpKeteranganModified()) {
                _dirtyCount ++;
                if (bean.getTbptkpKeterangan() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_keterangan IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_keterangan ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isTbptkpStatusModified()) {
                _dirtyCount ++;
                if (bean.getTbptkpStatus() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_status IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_status ").append(sqlEqualsOperation).append("?");
                }
            }
            if (bean.isTbptkpIdModified()) {
                _dirtyCount ++;
                if (bean.getTbptkpId() == null) {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_id IS NULL");
                } else {
                    sqlWhere.append((sqlWhere.length() == 0) ? " " : " AND ").append("tbptkp_id = ?");
                }
            }
        }
        finally
        {
            sqlEqualsOperation = null;
        }
        return _dirtyCount;
    }

    /**
     * fill the given prepared statement with the bean values and a search type
     * @param ps the preparedStatement that will be filled
     * @param bean the bean to use for creating the where clausis
     * @param searchType exact ?  like ? starting like ?
     * @return the number of clausis returned
     * @throws DAOException
     */
    protected int fillPreparedStatement(PreparedStatement ps, TbPtkpBean bean, int searchType) throws DAOException
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        try
        {
            if (bean.isTbptkpJumlahModified()) {
                log.debug("Setting for " + _dirtyCount + " [" + bean.getTbptkpJumlah() + "]");
                if (bean.getTbptkpJumlah() == null) { ps.setNull(++_dirtyCount, Types.DOUBLE); } else { Manager.setDouble(ps, ++_dirtyCount, bean.getTbptkpJumlah()); }
            }
            if (bean.isTbptkpKeteranganModified()) {
                switch (searchType){
                    case SEARCH_EXACT:
                        log.debug("Setting for " + _dirtyCount + " [" + bean.getTbptkpKeterangan() + "]");
                        if (bean.getTbptkpKeterangan() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getTbptkpKeterangan()); }
                        break;
                    case SEARCH_LIKE:
                        log.debug("Setting for " + _dirtyCount + " [%" + bean.getTbptkpKeterangan() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getTbptkpKeterangan() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        log.debug("Setting for " + _dirtyCount + " [" + bean.getTbptkpKeterangan() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getTbptkpKeterangan());
                        break;
                    case SEARCH_ENDING_LIKE:
                        log.debug("Setting for " + _dirtyCount + " [%" + bean.getTbptkpKeterangan() + "]");
                        if (bean.getTbptkpKeterangan() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getTbptkpKeterangan() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
            if (bean.isTbptkpStatusModified()) {
                switch (searchType){
                    case SEARCH_EXACT:
                        log.debug("Setting for " + _dirtyCount + " [" + bean.getTbptkpStatus() + "]");
                        if (bean.getTbptkpStatus() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getTbptkpStatus()); }
                        break;
                    case SEARCH_LIKE:
                        log.debug("Setting for " + _dirtyCount + " [%" + bean.getTbptkpStatus() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getTbptkpStatus() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        log.debug("Setting for " + _dirtyCount + " [" + bean.getTbptkpStatus() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getTbptkpStatus());
                        break;
                    case SEARCH_ENDING_LIKE:
                        log.debug("Setting for " + _dirtyCount + " [%" + bean.getTbptkpStatus() + "]");
                        if (bean.getTbptkpStatus() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getTbptkpStatus() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
            if (bean.isTbptkpIdModified()) {
                log.debug("Setting for " + _dirtyCount + " [" + bean.getTbptkpId() + "]");
                if (bean.getTbptkpId() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getTbptkpId()); }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        return _dirtyCount;
    }


