    //30
    public ViewMenuBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        ViewMenuBean bean = this.createViewMenuBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_TBM_NAMA_PARENT:
                        ++pos;
                        bean.setTbmNamaParent(rs.getString(pos));
                        break;
                    case ID_TBM_DISABLED:
                        ++pos;
                        bean.setTbmDisabled(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBM_INDEX:
                        ++pos;
                        bean.setTbmIndex(rs.getString(pos));
                        break;
                    case ID_TBM_NAMA:
                        ++pos;
                        bean.setTbmNama(rs.getString(pos));
                        break;
                    case ID_TBM_PARENT_ID:
                        ++pos;
                        bean.setTbmParentId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBM_ID:
                        ++pos;
                        bean.setTbmId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

