    /**
     * Transforms a ResultSet iterating on the view_vacancy table on a ViewVacancyBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting ViewVacancyBean bean
     * @throws DAOException
     */
    //30
    public ViewVacancyBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        ViewVacancyBean bean = this.createViewVacancyBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_COUNT_TBA_ID:
                        ++pos;
                        bean.setCountTbaId(Manager.getLong(rs, pos));
                        break;
                    case ID_TBJT_NAME:
                        ++pos;
                        bean.setTbjtName(rs.getString(pos));
                        break;
                    case ID_TBJT_ID:
                        ++pos;
                        bean.setTbjtId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBV_PUBLISH_WEB:
                        ++pos;
                        bean.setTbvPublishWeb(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBV_PUBLISH_RSS:
                        ++pos;
                        bean.setTbvPublishRss(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBV_ACTIVE:
                        ++pos;
                        bean.setTbvActive(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBV_DESCRIPTION:
                        ++pos;
                        bean.setTbvDescription(rs.getString(pos));
                        break;
                    case ID_TBV_NO_POSITIONS:
                        ++pos;
                        bean.setTbvNoPositions(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBV_NAME:
                        ++pos;
                        bean.setTbvName(rs.getString(pos));
                        break;
                    case ID_TBV_VACANCY_ID:
                        ++pos;
                        bean.setTbvVacancyId(rs.getString(pos));
                        break;
                    case ID_TBV_ID:
                        ++pos;
                        bean.setTbvId(Manager.getInteger(rs, pos));
                        break;
                    case ID_TBE_NAME:
                        ++pos;
                        bean.setTbeName(rs.getString(pos));
                        break;
                    case ID_TBE_ID:
                        ++pos;
                        bean.setTbeId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

